import React from 'react'
import { useEffect } from 'react'
import api from '../utility';
import { useState } from 'react';
import FriendSuggestionUser from './FriendSuggestionUser';
import RecievedFriendRequests from './RecievedFriendRequests';
function AddFriend() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    useEffect(() => {
        setLoading(true);
        async function getAllUsers() {
            const res = await api.get('user/getAllUser');
            if (res.status === 200) {
                if (res.data.users.length > 0) {
                    setUsers(res.data.users);
                } else {
                    setUsers([]);
                }
                setLoading(false);
            } else if (res.status === 500) {
                setUsers([]);
                setError("There is Some Internal Error");
                setLoading(false);
            }
        }
        getAllUsers();
    }, []);
    useEffect(() => {
        async function getRequests() {
            const res = await api.get('friend/getallRecievedFriendRequests');
            if (res.status === 200) {
                setRequests()
            } else if (res.status === 400) {

            }
        }
        getRequests();
    }, []);

    return (
        <div className='flex justify-center items-center w-full'>
            <div className='p-5 shadow-lg shadow-grey-800 mr-5'>
                <h1 className='text-xl font-bold'>
                    Suggested
                </h1>
                <div className=''>
                    {
                        loading ? <div>Loading...</div> :
                            error ? <div>{error}</div> :
                                users.length === 0 ? <div>No Users to show </div> :
                                    <div>
                                        {users.map(user => {
                                            return (
                                                <FriendSuggestionUser user={user}></FriendSuggestionUser>
                                            )
                                        })}
                                    </div>
                    }
                </div>
            </div>
            <div className='shadow-lg shadow-grey-800 p-2'>

            </div>


        </div>
    )
}

export default AddFriend
