import React from 'react'
import { useEffect } from 'react'
import api from '../utility';
import { useState } from 'react';
function AddFriend() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    useEffect(() => {
        setLoading(true);
        async function getAllUsers() {
            const res = await api.get('user/getAllUser');
            if (res.status === 200) {
                if (res.data.users.length > 0) {
                    setUsers(res.data.users);
                } else {
                    setUsers([]);
                }
                setLoading(false);
            } else if (res.status === 500) {
                setUsers([]);
                setError("There is Some Internal Error");
                setLoading(false);
            }
        }
        getAllUsers();
    }, []);
    return (
        <div className='flex justify-center items-center w-full'>
            <div className='p-5 shadow-lg shadow-grey-800'>
                <h1 className='text-2xl font-bold'>
                    Here are Friends
                </h1>
                <div className=''>
                    {
                        loading ? <div>Loading...</div> : 
                        error ? <div>{error}</div> :
                        users.length === 0 ? <div>No Users to show </div>:
                        <div>
                            {users.map(user => {
                                return (
                                    <div className='flex items-center jsutify-between p-2 border-b-2 border-grey-800'>
                                        <div>

                                        </div>
                                    </div>
                                )
                            })}
                        </div>
                    }
                </div>
            </div>
        </div>
    )
}

export default AddFriend
