import React from 'react'
import ReactDOM from 'react-dom';
function Videos(props) {
    const handleVideoClick = (e) => {
        e.preventDefault();
        e.target.muted = !e.target.muted;
    }
    const handleScroll = (e) => {
        let next = ReactDOM.findDOMNode(e.target).parentNode.nextSibling;
        console.log(next);
        if(next) {
            console.log("Ended");
            next.scrollIntoView();
            e.target.muted = true;
        }
    }
  return (
        <video onEnded={handleScroll} src = {props.videoLink} className='mb-5 h-[80vh] w-[35vw] shadow-lg shadow-grey-800 video-styling' onClick = {handleVideoClick} muted controls>
        </video>
  )
}

export default Videos
