import { Button } from '@mui/material'
import React from 'react'
import { useContext } from 'react'
import Authcontext from './AuthContextProvider';
import { Avatar } from '@mui/material'
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
function Navbar() {
    const [showMenu, setShowMenu] = useState(false);
    const userData = useContext(Authcontext);
    const history=useNavigate();
    const handleAvatarClick = () => {
        setShowMenu(!showMenu);
    }
    const handleLogOut = () => {
        userData.setUser("");
        localStorage.clear();
        setShowMenu(!showMenu);

        history('/');
    }
    const handleProfileClick = () => {
        setShowMenu(!showMenu);
        history('/profile');
    }
    return (
        <div>
            <div className='flex shadow-lg shadow-grey-800 text-black   h-16 justify-between items-center w-full'>
                <img href="/" src="https://www.logo.wine/a/logo/Instagram/Instagram-Wordmark-Logo.wine.svg" className='h-20 w-40 mix-blend-multiply cursor-pointer' />
                <div>
                    {userData.user ?
                        <ul className='md:flex'>
                            <li>
                                <Link to='/feed'>
                                    <img className='shadow-sm shadow-grey-800 rounded-lg w-10 h-10 mr-5 cursor-poniter' src="https://cdn.iconscout.com/icon/free/png-256/free-instagram-reel-4560268-3789542.png" />
                                </Link>
                            </li>
                            <li>
                                <Avatar onClick={handleAvatarClick} className='cursor-pointer mr-5' alt={userData.user.fullName} src={userData.user.profilePic} />
                            </li>
                        </ul>
                        : <div></div>
                    }
                </div>
            </div>
            <div className={showMenu ? 'absolute top-15 right-5 height-[100px] width-[50px] shadow-lg shadow-grey-800' : 'hidden'}>
                <div OnClick = {handleProfileClick} className='px-5 py-2 text-xl border-b-2 cursor-pointer'>
                    Profile
                </div>
                <div onClick = {handleLogOut} className='px-5 py-2 text-xl cursor-pointer'>
                    Logout
                </div>
            </div>
        </div>

    )
}

export default Navbar
