import React from 'react'
import ReactDOM from 'react-dom';
function Videos(props) {
    const handleVideoClick = (e) => {
        e.preventDefault();
        e.target.muted = !e.target.muted;
    }
    const handleScroll = (e) => {
        let next = ReactDOM.findDOMNode(e.target).parentNode.nextSibling;
        console.log(next);
        if (next) {
            console.log("Ended");
            next.scrollIntoView({ behavior: "smooth", inline: "nearest" });
            e.target.muted = true;
        }
    }
    return (
        <div className='mb-10'>
            <div>
            <video onEnded={handleScroll} className='h-[70vh] w-[100vw] md:w-[35vw]  shadow-lg shadow-grey-800 video-styling' onClick={handleVideoClick} muted autoPlay loop>
                <source src={`http://localhost:8080/video/${props.videoLink}`}></source>
            </video>
            <div className='shadow-lg shadow-grey-800 px-10 flex justify-between items-center mb-5 h-[50px]'>
                <div className="material-symbols-outlined cursor-pointer">
                    thumb_up
                </div>
                <div className="material-symbols-outlined cursor-pointer">
                    comment
                </div>
            </div>
            </div>
        </div>
    )
}

export default Videos
