import React from 'react'
import ReactDOM from 'react-dom';
function Videos(props) {
    const handleVideoClick = (e) => {
        e.preventDefault();
        e.target.muted = !e.target.muted;
    }
    const handleScroll = (e) => {
        let next = ReactDOM.findDOMNode(e.target).parentNode.nextSibling;
        console.log(next);
        if (next) {
            console.log("Ended");
            next.scrollIntoView({ behavior: "smooth", inline: "nearest" });
            e.target.muted = true;
        }
    }
    return (
        <div className='mb-10'>
            <video onEnded={handleScroll} className='h-[80vh] w-[100vw] md:w-[35vw]  shadow-lg shadow-grey-800 video-styling' onClick={handleVideoClick} muted autoPlay loop>
                <source src={`http://localhost:8080/video/${props.videoLink}`}></source>
            </video>
            <div className='mt-[-50px] mb-5 h-[50px]'>
                <span class="material-symbols-outlined">
                    thumb_up
                </span>
                <span class="material-symbols-outlined">
                    comment
                </span>
            </div>
        </div>
    )
}

export default Videos
