    const userData = useContext(Authcontext);
    const [commentShow,setcommentShow] = useState(false);

    const handleVideoClick = (e) => {
        e.preventDefault();
        e.target.muted = !e.target.muted;
    }
    const handleScroll = (e) => {
        let next = ReactDOM.findDOMNode(e.target).parentNode.nextSibling;
        console.log(next);
        if (next) {
            console.log("Ended");
            next.scrollIntoView({ behavior: "smooth", inline: "nearest" });
            e.target.muted = true;
        }
    }
    const handleLikeClick = () => {

    }

    const handleCommentClick = () => {

    }
    return (
        <div className='mb-10 flex'>
            {props.post ? <div className='p-[10%] flex flex-col justify-between items-center'>
                <video onEnded={handleScroll} className='h-[40vh] w-[40vw] md:w-[35vw]  shadow-sm shadow-grey-800 video-styling' onClick={handleVideoClick} muted autoPlay>
                    <source src={`http://localhost:8080/video/${props.post.video_url}`}></source>
                </video>
                <div className='shadow-lg shadow-grey-800 px-5 flex justify-between items-center mb-5 h-[50px]'>
                    <div className='flex'>
                        <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
                        <span className='h-fit mx-2 text-sky-800 bg-slate-300 p-2'>{props.post.fullName}</span>
                        <Button>Follow</Button>
                    </div>

                    <div>
                        <div onClick = {handleLikeClick} className="mr-5 material-symbols-outlined cursor-pointer">
                            thumb_up
                        </div>
                        <div onClick = {handleCommentClick} className="material-symbols-outlined cursor-pointer">
                            comment
                        </div>
                    </div>
                </div>
            </div> : <div>Loading...</div>}
            <div className={setcommentShow ? "flex h-[73vh] w-[25vw] bg-black" : "hidden"}>
            </div>
        </div>
    )
}

